mapper.insert:
/* QueryId : common.insertServerMaster */
INSERT INTO SERVER_MASTER
	(
		SERVER_ID
	  , SERVER_NAME
	)
VALUES
	(
		#{serverId}
	  , #{serverName}
	)
ON DUPLICATE KEY UPDATE
	SERVER_NAME = #{serverName}

/* QueryId : common.insertJobMaster */
INSERT INTO JOB_MASTER
(
	  JOB_ID
	, JOB_GROW_ID
	, JOB_NAME
	, JOB_GROW_NAME
	, PARENT_JOB_ORDER
	, JOB_GROW_ORDER
	, JOB_ORDER
)
VALUES
(
	  #{jobId}
	, #{jobGrowId}
	, #{jobName}
	, #{jobGrowName}
	, #{parentJobOrder}
	, #{jobGrowOrder}
	, #{jobOrder}
)
ON DUPLICATE KEY UPDATE
	JOB_NAME = #{jobName}
  , JOB_GROW_NAME = #{jobGrowName}
  , PARENT_JOB_ORDER = #{parentJobOrder}
  , JOB_GROW_ORDER = #{jobGrowOrder}
  , JOB_ORDER = #{jobOrder}
